mapper.select:
SELECT ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NAME
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail} AND MEMBER_PASSWORD = #{memberPassword}

mapper.update:
UPDATE TBL_MEMBER
SET
MEMBER_EMAIL = #{memberEmail},
MEMBER_NAME = #{memberName},
MEMBER_PASSWORD = #{memberPassword}
WHERE ID = #{id}
